/* Margin : espace extérieur */
p {
  margin: 20px; /* 20px de tous les côtés */
  margin-top: 30px;
  margin-right: 15px;
  margin-bottom: 10px;
  margin-left: 15px;
}

/* Padding : espace intérieur */
.boite {
  padding: 15px; /* 15px de tous les côtés */
  padding: 10px 20px; /* 10px vertical, 20px horizontal */
  border: 2px solid #000;
  background-color: #ddd;
}

/* Box Model complet */
.exemple {
  width: 200px;
  padding: 20px;    /* Intérieur */
  border: 5px solid black;
  margin: 30px;     /* Extérieur */
}

/* Balises BLOCK par défaut */
div, p, h1, ul, li {
  /* Prennent toute la largeur */
  /* Commencent sur nouvelle ligne */
}

/* Balises INLINE par défaut */
span, a, strong, em {
  /* Prennent juste la largeur du contenu */
  /* S'alignent horizontalement */
}

/* Transformer inline en block */
span {
  display: block;
  width: 200px;
  margin: 10px;
  padding: 10px;
}

/* Transformer block en inline */
div {
  display: inline;
}

/* Inline-block : le meilleur des deux */
.boite {
  display: inline-block;
  width: 150px;
  height: 150px;
  margin: 10px;
  padding: 20px;
  background-color: #3b82f6;
  color: white;
}

/* Container Flexbox */
.container {
  display: flex;
  flex-direction: row; /* ou column */
  justify-content: space-between; /* flex-start, center, flex-end, space-around */
  align-items: center; /* flex-start, center, flex-end, stretch */
  gap: 20px;
  flex-wrap: wrap; /* pour passer à la ligne */
}

/* Exemple : Menu horizontal */
.menu {
  display: flex;
  justify-content: space-around;
  align-items: center;
  background-color: #333;
  padding: 15px;
}

.menu a {
  color: white;
  text-decoration: none;
  padding: 10px 20px;
}

/* Exemple : Cards alignées */
.cards {
  display: flex;
  gap: 20px;
  flex-wrap: wrap;
}

.card {
  flex: 1;
  min-width: 250px;
  padding: 20px;
  border: 1px solid #ddd;
}

/* Container Grid */
.container {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr; /* 3 colonnes égales */
  grid-template-columns: 200px 1fr 200px; /* colonnes fixes et flexible */
  grid-template-columns: repeat(3, 1fr); /* répéter 3 fois */
  gap: 20px; /* espace entre les éléments */
}

/* Exemple : Layout 3 colonnes */
.gallery {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 15px;
}

.gallery img {
  width: 100%;
  height: auto;
}

/* Exemple : Layout complexe */
.layout {
  display: grid;
  grid-template-columns: 250px 1fr;
  grid-template-rows: auto 1fr auto;
  gap: 20px;
  height: 100vh;
}

.header {
  grid-column: 1 / 3; /* s'étend sur 2 colonnes */
}

.sidebar {
  grid-column: 1 / 2;
}

.content {
  grid-column: 2 / 3;
}

.footer {
  grid-column: 1 / 3;
}
